import React, { Component } from 'react';


class CryptoAPI extends Component {

  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      name: '',
      price: ''
    };
  }

  componentDidMount() {
    fetch("https://coingecko.p.rapidapi.com/coins/bitcoin", {
      method: 'GET',
      headers: {
        'x-rapidapi-host': 'coingecko.p.rapidapi.com',
        'x-rapidapi-key': 'f499b0c80emsh4da670db83bbe25p1c6b2bjsncb9ae6435440'
      }
    })
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            name: result.symbol,
            price: result.market_data.current_price.usd
          });
          console.log(result);
        },

        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
  }

  render() {
    const { error, isLoaded, price, name } = this.state;
    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <div>Loading...</div>;
    } else {
      return (
        <ul>
        <li key={item.name}>
          {item.name} {item.market_data.current_price.usd}
        </li>
        </ul>
      );
    }
  }

}


export default CryptoAPI;
